mapper.update:
update project p join team t on p.no = t.no
set title = #{title}, detail = #{detail}, start_date = #{start_date}, end_date = #{end_date}, mod_date = now()
where p.user_id = #{user_id} and p.no = #{no}

update team
set status = 'ACCEPT'
where no = #{no}
  and peer_id = #{user_id}

update team
set status = 'DECLINE'
where no = #{no} and peer_id = #{user_id}
